import { useState } from "react";

export default function Tic_Tac_Toe() {
  const [gameStatus, setGameStatus] = useState(
    [" "],
    [" "],
    [" "],
    [" "],
    [" "],
    [" "],
    [" "],
    [" "],
    [" "]
  );

  const [player, setPlayer] = useState(1)

  function handleChange(currentIndex)
  {
    player === 1 
    ? setGameStatus(gameStatus[currentIndex] = "x") 
    : gameStatus[currentIndex] = 'O'

  }

  return (
    <div className="wrapper">
      <div class="board">
        <div onClick={handleChange(0)} class="board_tile remove-left-border remove-top-border">
            {gameStatus[0]}
            </div>
        <div onClick={handleChange(1)} class="board_tile remove-top-border">
            {gameStatus[1]}
            </div>
        <div onClick={handleChange(2)} class="board_tile remove-right-border remove-top-border">
            {gameStatus[2]}
            </div>
        <div onClick={handleChange(3)} class="board_tile remove-left-border">
            {gameStatus[3]}
            </div>
        <div onClick={handleChange(4)} class="board_tile">
            {gameStatus[4]}
            </div>
        <div onClick={handleChange(5)} class="board_tile remove-right-border">
            {gameStatus[5]}
            </div>
        <div onClick={handleChange(6)} class="board_tile remove-left-border remove-bottom-border">
            {gameStatus[6]}
            </div>
        <div onClick={handleChange(7)} class="board_tile remove-bottom-border">
            {gameStatus[7]}
            </div>
        <div onClick={handleChange(8)} class="board_tile remove-right-border remove-bottom-border">
            {gameStatus[8]}
            </div>
      </div>
    </div>
  );
}
